import React from "react";
import { observer, inject } from "mobx-react";
import { Input, Tabs, Form, Button, Table,Divider, Result, Modal, message, Skeleton } from "antd";
import clone from 'util/clone'
import * as DT  from 'util/date'

import "./index.less";
import moment from "moment";
import { toJS } from "mobx";

const { TextArea } = Input;
var _val

// 申请加梯
@inject('appyActions', 'appyStore')
@observer
class Appy extends React.Component {
  constructor(props) {
    super(props);
    this.action = this.props.appyActions
    this.store  = this.props.appyStore
    this.state = {
      loading: false,
    }
  }


  async UNSAFE_componentWillMount() {
    let params = {}

    this.setState({ loading: true })
    await this.action.getApply(params)
    this.setState({ loading: false })
  }



  render() {
    const {loading} = this.state
    const apply = toJS(this.store.apply)
    const columns = [{
        title: '状态',
        dataIndex: 'stat',
        filters: [
          {
            text: '已审查',
            value:'已审查',
          },{
            text: '申请中',
            value: '申请中',
          }
        ],
        onFilter: (value, record) => record.stat  === value
      },{
        title: '时间',
        dataIndex: 'apdt',
        defaultSortOrder: 'descend',
        sorter: (a, b) => a.apdt - b.apdt,
      },{
        title: '申请人',
        dataIndex: 'name',
        key: 'name',
        ...this.getColumnSearchProps('name'),
      },{
        title: '联系电话',
        dataIndex: 'phon',
        key: 'phon',
        ...this.getColumnSearchProps('phon'),
      },{
        title: '加梯地址',
        dataIndex: 'addr',
        key: 'addr',
        ...this.getColumnSearchProps('addr'),
      },{
        title: '功能',
        key: 'action',
        render: (text, record) => (
          <Button type="primary">同意加梯</Button>
        ),
      },
    ];


    return (
      <div className='g-appy'>
        <div className="m-appy-menu">
          <Button type="primary">导出Excel</Button>
        </div>
        <Table size='small' dataSource={apply} columns={columns} />;
      </div>
    )
  }
}


export default Form.create()(Appy);
